String.prototype.capitalize=function(){const t=this.valueOf();return t.charAt(0).toUpperCase()+t.substr(1)},String.prototype.capitalizeWords=function(){return this.valueOf().replace(/\w\S*/g,(function(t){return t.capitalize()}))},$o("form button[type=submit]").addEventListener("mouseup",(function(t){const e=$o("form input[name=name]");e.value=e.value.capitalizeWords()})),$o("form textarea").addEventListener("keyup",(function(t){t.target.value.length<5&&(t.target.value=t.target.value.capitalize())}));
