String.prototype.capitalize=function(){return this.valueOf().replace(/\w\S*/g,(function(e){return e.charAt(0).toUpperCase()+e.substr(1)}))},$o("form input[name=name]").addEventListener("keyup",(function(e){e.target.value=e.target.value.capitalize()})),$o("form textarea").addEventListener("keyup",(function(e){e.target.value.length<5&&(e.target.value=e.target.value.capitalize())}));
