const dds=document.getElementsByClassName("dropdown-toggle"),ddmenus=document.getElementsByClassName("dropdown-menu");function hideDDMenus(e){for(let d=0;d<ddmenus.length;d++)ddmenus[d]!==e&&ddmenus[d].classList.remove("show")}for(let e=0;e<dds.length;e++)dds[e].addEventListener("click",(function(){let d=ddmenus[e];hideDDMenus(d),d.classList.toggle("show")}));
