const dds=document.getElementsByClassName("dropdown-toggle"),ddmenus=document.getElementsByClassName("dropdown-menu");function toggleDisplay(e){"block"===e.style.display?e.style.display="":e.style.display="block"}function hideDDMenus(e){for(let d=0;d<ddmenus.length;d++)ddmenus[d]!==e&&(ddmenus[d].style.display="")}for(let e=0;e<dds.length;e++)dds[e].addEventListener("click",(function(){let d=ddmenus[e];hideDDMenus(d),toggleDisplay(d)}));
